import axios from "axios";
import { useState } from "react"
import { useEffect } from "react"
 import "./trending.css"

export const Trending  = ()=>{

  const [movie,setMovie] = useState();

   useEffect(()=>{

        axios
        .get("https://api.themoviedb.org/3/trending/movie/day?api_key=b44716bfb3d62913eb0e7024880e9455")
         .then((data)=>{
             console.log(data.data.results)
             setMovie(data.data.results)
         })            
         .catch((error)=>{
             console.log(error)
         })


   },[])




return (
  <div
    className="sliderDiv"
    class="mt-[2rem] w-[100vm] overflow-hidden  shadow shadow-blue-500/40 hover:shadow-indigo-500/40"
  >
    <h1 class="text-white  text-[40px]   ">Trending Movies</h1>

    {movie ? (
      <div className="slider">
        {movie.map((e, i) => (
          <div
            key={i}
            class=" slide-track  hover:cursor-pointer hover:w-[35rem]"
          >
            <img
              src={`https://image.tmdb.org/t/p/w500${e.poster_path}
`}
              alt=""
            />
            <h3>{e.original_title}</h3>
          </div>
        ))}
      </div>
    ) : (
      ""
    )}
  </div>
);



}
