import React, { Component } from "react";
import { caroselData } from "./CarouselData";
import { AiOutlineLeft, AiOutlineRight } from "react-icons/ai";
import Swipe from "react-easy-swipe";

class Carousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentSlide: 0,
    };


 const  nextSlide = () => {
  let newSlide =
    this.state.currentSlide === CarouselData.length - 1
      ? 0
      : this.state.currentSlide + 1;
  this.setState({ currentSlide: newSlide });
};

prevSlide = () => {
  let newSlide =
    this.state.currentSlide === 0
      ? CarouselData.length - 1
      : this.state.currentSlide - 1;
  this.setState({ currentSlide: newSlide });
}; 



  }

  render() {
    return (
      <div className="mt-8">
        <div className="max-w-lg h-72 flex overflow-hidden relative">
            <AiOutlineLeft   onClick={this.prevSlide}    className='absolute left-0 text-3xl inset-y-1/2 text-white cursor-pointer' />

           <AiOutlineRight onClick={this.prevSlide}  className='absolute right-0 text-3xl inset-y-1/2 text-white cursor-pointer' />
          {caroselData.map((slide, index) => {
            return (
              <img
                src={slide.image}
                alt="This is a carousel slide"
                key={index}
                className={
                  index === this.state.currentSlide
                    ? "block w-full h-auto object-cover"
                    : "hidden"
                }
              />
            );
          })}
        </div>
      </div>
    );
  }
}


export default Carousel;
