import React from "react";
import { useLocation } from "react-router-dom";
import { connect, useSelector } from "react-redux";
import { useEffect } from "react";
import {fetchMovieDetail} from "../redux/action";
import Loading from "./Loading";
// import  useSelector  from "react-redux";

 const MovieDetail = (props)=>{

const location = useLocation();
// console.log(location.state.id)
const movieDetail = useSelector((state)=> state.detail.data)
const loading = useSelector((state)=> state.detail.loading)
const video = movieDetail.videos.results[0].key;
console.log(video)
useEffect(()=>{

    props.fetchMovieDetail(location.state.id)

},[])

return (loading===true ? (<Loading></Loading>) : (

<div>
    <h1>this is movieDetail</h1>

    <div className="movie ml-[10rem]">
      <iframe
        width="1000"
        height="500"
        src={`https://www.youtube.com/embed/${video}`}
        title="YouTube video player"
        frameborder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
        allowfullscreen
      ></iframe>
    </div>
  </div>


) )









}


export default connect(null, {fetchMovieDetail})(MovieDetail)
