const intialStore = {
  loading: false,
  data: [],
  error: "",
};

const reducer = (store = intialStore, action) => {
  switch (action.type) {
    case "FETCH_USER_REQUEST":
      return {
        ...store,
        loading: true,
    };



   case "FETCH_DATA":
       return {
           ...store,
           loading:false,
           data : [action.payoad]
       }







    default:
      return {
        ...store,
      };
  }
};

export default reducer;
