import { useState } from "react"
import * as AiIcons from "react-icons/ai"



import { SidebarData } from "./SidebarData";

export const Navbar = ()=>{
  const [search, setSearch ] = useState(false)
  const [sidebar, setSidebar] = useState(false); 


  const showSidebar = ()=>{
       setSidebar(!sidebar)  
  }  



     return(
      <>


     <div class="flex flex-row fixed z-10  bg-transparent mb-[2rem]  w-[80rem] items-center  hover:bg-navbac  mb-[2rem] ... " >

      <div class="m-1 flex flex-row ml-14" ><img  onClick={showSidebar} src="https://play.hbomax.com/assets/images/primary_nav/desktop/browse_default_max.svg" alt="" />

      {sidebar ? (<div class="absolute w-[20rem] h-[120vh] ml-[-4rem] mt-[-1.2rem] bg-[blue] ">

          <ul class="flex flex-col center h-[20rem] mt-[5rem] px-[8rem] space-y-[4rem]">
           <li class="px-[8rem]  " onClick={showSidebar}><AiIcons.AiOutlineClose></AiIcons.AiOutlineClose></li> 
            <li class="  hover:bg-[indigo] hover:block">Home</li>
            <li class="  hover:bg-[indigo]" >Series</li>
             <li class="  hover:bg-[indigo]">Movies</li>
             <li class="  hover:bg-[indigo]">Originals</li>
             <li class="  hover:bg-[indigo]">Just Added</li>
             <li class="  hover:bg-[indigo]">Last Chance</li>
             <li class="  hover:bg-[indigo]">Coming Soon</li>
             <li class="  hover:bg-[indigo]">Coming Soon</li>


          </ul>



      </div>) :(<div class="absolute w-[20rem] h-[120vh] translate-x-[-100%] ml-[-4rem] mt-[-1.2rem] bg-[blue] ">

          <ul class="flex flex-col center h-[20rem] mt-[5rem] px-[8rem] space-y-[4rem]">
           <li class="px-[8rem]  " onClick={showSidebar}><AiIcons.AiOutlineClose></AiIcons.AiOutlineClose></li> 
            <li class="  hover:bg-[indigo] hover:block">Home</li>
            <li class="  hover:bg-[indigo]" >About</li>
             <li class="  hover:bg-[indigo]">Contact</li>
             <li class="  hover:bg-[indigo]">Mail us</li>
             <li class="  hover:bg-[indigo]">Find us</li>


          </ul>



      </div>)}






    <div class="m-4 ml-10" onClick={()=>{
        if(search){
          setSearch(false)
        }
        else{
          setSearch(true)
        }
      }}    ><img src="https://play.hbomax.com/assets/images/primary_nav/desktop/search_default_max.svg" alt="" /></div>



    {search ? (<div class="block  "><input type="text"  class="w-[18rem] h-[2.5rem] rounded-md mt-[0.5rem] " placeholder="what are you looking for?" /></div>)

      :  (<div class="hidden"><input type="text" placeholder="what are you looking for?" /></div>)

  }




      </div>








      <div class="mt-[0.3rem] mx-auto mr-60     "><img src="https://play.hbomax.com/assets/images/branding/desktop/hbomax/dt-hbomax-logo-in_app.svg" alt="" /></div>
      <div class="mt-[0.3rem] mr-3 color-navfont font-bold">  <h3 class="text-[#FFFFFFCC]">Subscribe Now</h3></div>
      <div class="mt-[0.3rem] mx-24 color-navfont fo" > <h3 class="text-[#FFFFFFCC]">Sign In</h3></div>
     </div>







</>

     )




