import { useState } from "react"
import * as AiIcons from "react-icons/ai"

export const Navbar = ()=>{
  const [search, setSearch ] = useState(false)
  const [sidebar, setSidebar] = useState(false); 


  const showSidebar = ()=>{
       setSidebar(!sidebar)  
  }  



     return(
      <>


     <div class="flex flex-row fixed z-10  bg-transparent mb-[2rem]  w-[80rem] items-center  hover:bg-navbac  mb-[2rem] ... " >

      <div class="m-1 flex flex-row ml-14" onClick={showSidebar}><img src="https://play.hbomax.com/assets/images/primary_nav/desktop/browse_default_max.svg" alt="" />






    <div class="m-4 ml-10" onClick={()=>{
        if(search){
          setSearch(false)
        }
        else{
          setSearch(true)
        }
      }}    ><img src="https://play.hbomax.com/assets/images/primary_nav/desktop/search_default_max.svg" alt="" /></div>



    {search ? (<div class="block  "><input type="text"  class="w-[18rem] h-[2.5rem] rounded-md mt-[0.5rem] " placeholder="what are you looking for?" /></div>)

      :  (<div class="hidden"><input type="text" placeholder="what are you looking for?" /></div>)

  }




      </div>








      <div class="mt-[0.3rem] mx-auto mr-60     "><img src="https://play.hbomax.com/assets/images/branding/desktop/hbomax/dt-hbomax-logo-in_app.svg" alt="" /></div>
      <div class="mt-[0.3rem] mr-3 color-navfont font-bold">  <h3 class="text-[#FFFFFFCC]">Subscribe Now</h3></div>
      <div class="mt-[0.3rem] mx-24 color-navfont fo" > <h3 class="text-[#FFFFFFCC]">Sign In</h3></div>
     </div>

  <div>
   {sidebar ? (<ul class="border-2 inline flex">

           <li>Movies</li>    
           <li>Music</li>    
           <li>Porn</li>    
           <li>Anime</li>    



    </ul>) : ""}
</div>





</>

     )




}
