import { fetch } from "../redux/action";
import { useSelector } from "react-redux";

 const Trending  = (props)=>{

  // const [movie,setMovie] = useState();

   const movie = useSelector((state)=> state.data)

   useEffect(()=>{


      props.fetch()
















    // axios
