import React from "react";
import { connect, useSelector } from "react-redux";
import { useEffect } from "react";
import { fetchMovieDetail } from "../redux/action";
import Loading from "./Loading";
import { useLocation } from "react-router-dom";
import { Navbar } from "../components/Navbar";
import background from "../images/background/background.png";

const MovieDetail = (props) => {
  const location = useLocation();
  const id = location.state;
  // console.log(id)
  useEffect(() => {
    props.fetchMovieDetail(id);
  }, []);

  // console.log(location.state.id)
  const movieDetail = useSelector((state) => state.detail.data);
  console.log(movieDetail);
  const loading = useSelector((state) => state.detail.loading);

  return loading === true ? (
    <Loading></Loading>
  ) : (
    <div className="mt-0">
      <Navbar></Navbar>
      <div
        className="py-[6rem]"
        style={{
          backgroundImage: `url(${background})`,
        }}
      >
        {movieDetail.map(
          ({
            backdrop_path,
            budget,
            homepage,
            original_language,
            original_title,
            overview,
            poster_path,
            runtime,
            vote_count,
            genre,
            release_date,
            tagline,
            vote_average,
          }) => (
            <div className="border-4 flex justify-center  w-[30rem] m-auto  flex-col ... ">
              <div className=" h-[35rem] flex">
                <img
                  src={`https://image.tmdb.org/t/p/w500${poster_path}`}
                  alt=""
                />
              </div>
              <div className="flex justify-center">
                <h3 className="text-white text-5xl">{original_title}</h3>
              </div>

              <div>

               <h1 className="text-white text-2xl">Rating:  <span className="text-xl">{vote_average}</span></h1>

              </div>
            </div>
          )
        )}
      </div>
    </div>
  );
};

export default connect(null, { fetchMovieDetail })(MovieDetail);
